CREATE TABLE users(userid VARCHAR(50) PRIMARY KEY, password VARCHAR(50) NOT NULL, firstName VARCHAR(50), lastName VARCHAR(50));

CREATE TABLE songlist(id INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1 INCREMENT BY 1) PRIMARY KEY, ownerid VARCHAR(50), FOREIGN KEY (ownerid) REFERENCES users(userid), name VARCHAR(100), private BOOLEAN);

CREATE TABLE song(id INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1 INCREMENT BY 1) PRIMARY KEY, title VARCHAR(100) NOT NULL, artist VARCHAR(100), label VARCHAR(100), released INTEGER);

CREATE TABLE song_songlist(list_id INTEGER GENERATED BY DEFAULT AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY NOT NULL, song_id INTEGER NOT NULL, FOREIGN KEY (song_id) REFERENCES song(id));